import React, { Component } from "react";


class Paginator extends Component<{ lastPage: number; handlePageChange: any }> {
  state: {
    page: 1,
    lastPage: 0
  }


  componentDidUpdate(){
    if (this.props.lastPage !== this.state.lastPage){
      this.setState({page: 1});
    }
  }

  next = async () => {
    if (this.state.page === this.props.lastPage) {
      return;
    }

    this.setState({page: this.state.page++});
    this.props.handlePageChange(this.state.page);
  };

  previous = async () => {
    if (this.state.page === 1) {
      return;
    }

    this.setState({ page: this.state.page--});
    this.props.handlePageChange(this.state.page);
  };

  render() {
    return (
      <nav className="paginator__pagination">
        <li className="paginator__page-item">
          <button
            onClick={this.previous}
            className="paginator__pagination-previous"
            disabled={this.state.page === 1}
          >
            Back
          </button>
        </li>
        <li className="paginator__page-item">
          <button
            onClick={this.next}
            className="paginator__pagination-next"
            disabled={this.state.page === this.state.lastPage}
          >
            Next
          </button>
        </li>
      </nav>
    );
  }
}

export default Paginator;
