import React, { FC, useEffect } from 'react'
import { ActionCard } from '@island.is/service-portal/core'
import { Document } from '@island.is/api/schema'
import { useLazyDocumentDetail } from '@island.is/service-portal/graphql'
import { useLocale } from '@island.is/localization'
import { toast } from '@island.is/island-ui/core'

const base64ToArrayBuffer = (base64Pdf: string) => {
  let binaryString = window.atob(base64Pdf)
  let binaryLen = binaryString.length
  let bytes = new Uint8Array(binaryLen)
  for (let i = 0; i < binaryLen; i++) {
    let ascii = binaryString.charCodeAt(i)
    bytes[i] = ascii
  }
  return bytes
}

function showDocument(base64Pdf: string) {
  try {
    let byte = base64ToArrayBuffer(base64Pdf)
    let blob = new Blob([byte], { type: 'application/pdf' })
    window.open(URL.createObjectURL(blob), '_blank')
  } catch (error) {
    console.log(error)
  }
}

const isIos = () => {
  let ua = window.navigator.userAgent
  let iOS = !!ua.match(/iPad/i) || !!ua.match(/iPhone/i)
  let webkit = !!ua.match(/WebKit/i)
  return iOS && webkit && !ua.match(/CriOS/i)
}

const downloadAsPdf = (base64Pdf: string, fileName: string) => {
  if (typeof window === 'undefined') {
    return
  }
  // iosSafari is causing issues
  if (true) {
    showDocument(base64Pdf)
  } else if (true) {
    var reader = new FileReader()
    var out = new Blob([base64Pdf], { type: 'application/pdf' })
    reader.onload = function (e) {
      window.location.href = reader.result
    }
    reader.readAsDataURL(out)

    // var blob = new Blob([response.data], { type: "application/pdf" });
    var fileURL = URL.createObjectURL(out)
    var a = document.createElement('a')
    a.href = fileURL
    a.target = '_blank'
    a.download = 'lkn_' + fileName
    document.body.appendChild(a)
    a.click()
  } else {
    const fakeLink = window.document.createElement('a')
    const url = `data:application/pdf;base64,${base64Pdf}`
    fakeLink.href = url
    fakeLink.download = fileName
    fakeLink.title = fileName
    const clickHandler = () => {
      setTimeout(() => {
        URL.revokeObjectURL(url)
        fakeLink.removeEventListener('click', clickHandler)
      }, 150)
    }
    fakeLink.addEventListener('click', clickHandler, false)
    fakeLink.click()
  }
}

const openExternalDocument = (url: string) => {
  if (typeof window === 'undefined') {
    return
  }
  window.open(url, '_blank')
}

interface Props {
  document: Document
}

const DocumentCard: FC<Props> = ({ document }) => {
  const fileName = `fileName.pdf`
  const { formatMessage } = useLocale()
  const { fetchDocument, loading, data, error } = useLazyDocumentDetail(
    document.id,
  )
  useEffect(() => {
    if (data || error) {
      handleOnFetch()
    }
  }, [data, error])

  const handleOnFetch = () => {
    const fileName = `fileName.pdf`
    if ((data?.fileType || '').toLowerCase() === 'pdf' && data?.content) {
      downloadAsPdf(data.content, fileName)
      return
    }
    if (data?.url) {
      openExternalDocument(data.url)
      return
    }

    const doc =
      ''

    if (doc) {
      console.log('saekja doooooc')
      downloadAsPdf(doc, fileName)
    }

    if (error && !loading) {
      console.log('error')
      console.log(error)
      console.log(loading)
      toast.error(
        formatMessage({
          id: 'sp.documents:documentCard-error-singleDocument',
          defaultMessage: 'Ekki tókst að sækja skjal',
        }),
      )
    }
  }

  const handleOnClick = () => {
    if (loading) {
      return
    }
    if (!data) {
      fetchDocument()
    } else {
      handleOnFetch()
    }
  }

  return (
    <ActionCard
      title={document.subject}
      date={new Date(document.date)}
      label={document.senderName}
      key={document.id}
      loading={loading}
      cta={{
        onClick: handleOnClick,
        label: formatMessage({
          id: 'sp.documents:documentCard-ctaLabel',
          defaultMessage: 'Sækja skjal',
        }),
      }}
    />
  )
}

export default DocumentCard
